import React from 'react';
import ReactDOM from 'react-dom'
class Todo extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      name: 'Simple React TODO list',
      count: 0,
      tasks: ['just', 'simple', 'thing']
    }
    this.handleClick = this.handleClick.bind(this)
    this.handleClickIndex = this.handleClickIndex.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.handleSubmit = this.handleSubmit.bind(this)
  }
  handleClick(event){
    eval(this[event.target.name]).bind(this)(event)
  }
  handleClickIndex(index, event){
    eval(this[event.target.name]).bind(this)(index, event)
  }
  handleChange(event){
    eval(this[event.target.name]).bind(this)(event)
  }
  handleSubmit(event){
    event.preventDefault()
    eval(this[event.target.name]).bind(this)(event)
  }
  task(event) {
    this.setState({task:event.target.value})
  }
  addTask(event) {
    if (!this.state.task) return
    const tasks = this.state.tasks || []
    tasks.push(this.state.task)
    this.setState({tasks:tasks, task:''})
  }
  removeTask(index, event) {
    const tasks = this.state.tasks
    tasks.splice(index, 1)    
    this.setState({tasks})
  }
  render(){
    const tasks = (this.state.tasks||[]).map((task,index)=>(
      <li>
        {task} <button name="removeTask" onClick={event=>this.handleClickIndex(index,event)}>x</button>
      </li>
    ))
    return (
      <div className="container">
        <h1>{this.state.name}</h1>
        <div>
          <ol>
            {tasks}
            {
              this.state.task &&
              <li>{this.state.task}</li>
            }
          </ol>
          <div>
            <form name="sendTask" onSubmit={this.handleSubmit}>
              <input name="task" value={this.state.task} onChange={this.handleChange}/>
              <button type="submit" name="addTask" onClick={this.handleClick}>Add</button>
            </form>
          </div>
          <div style={{marginTop:'1rem'}}>
            <h5>More</h5>
            <ul>
              <li>See Angular 7 TODO list version <a href="https://codepen.io/mjunaidi/pen/VOpzga" target="_blank" rel="noopener noreferrer" title="Open in new tab">here</a></li>
              <li>See Vue JS TODO list version <a href="https://codepen.io/mjunaidi/pen/qLBjXa" target="_blank" rel="noopener noreferrer" title="Open in new tab">here</a></li>
              <li>See React TODO list version 2.0 <a href="https://codepen.io/mjunaidi/pen/MZKZOZ" target="_blank" rel="noopener noreferrer" title="Open in new tab">here</a></li>
              <li>See React TODO list version 3.0 <a href="https://codepen.io/mjunaidi/pen/dyoNzbX" target="_blank" rel="noopener noreferrer" title="Open in new tab">here</a></li>
            </ul>
          </div>
        </div>
      </div>
    )
  }
}

ReactDOM.render(
  <Todo/>,
  document.getElementById('root')
);
